statements.statement:

			select top 1 biId
            from psy.AEBBaseInformation bi
            where bi.biIk = {ik}
            and bi.biDataYear = {year}
            and bi.biStatusId = 10
			order by bi.biDataYear desc


			select top 1 biId
            from psy.AEBBaseInformation bi
            where bi.biIk = {ik}
            and bi.biDataYear <= {year}
            and bi.biStatusId = 10
			order by bi.biDataYear desc


            select biid
            from (
                select ROW_NUMBER() over (partition by bi.biIk order by bi.biDataYear desc) as 'rowNum', bi.biId
                from psy.AEBBaseInformation bi
                where bi.biIk in (
					select cuIk
					from(
						select cuik, ehPsyGroup, ROW_NUMBER() over (partition by biIk order by biDataYear desc) as rowNum
						from CallCenterDB.dbo.ccCustomer cu
						join psy.ExpectedHospital on cuIk = ehIk
						join psy.AEBBaseInformation on cuIk = biik and ehYear = biDataYear
						where cuIsTest = 0
						and cuIsActive = 1
						and cuik not in (select hcexIk from psy.HospitalComparisonExcludeIK)
						and case when cu.cuPsyStateId = 255 then cu.cuStateId else cu.cuPsyStateId end in ({stateIds})
					) i
					where rowNum = 1 and ehPsyGroup = {psyGroupId}
                )
                and bi.biDataYear <= {year}
                and bi.biStatusId = 10
            ) a
            where a.rowNum = 1
